import { EventService } from './event-service';
import { throttle } from '../utils/throttle';
import { debounce } from '../utils/debounce';
class WindowEventService {
    constructor() {
        this.setupBroadcastEvents();
    }
    setupBroadcastEvents() {
        window.addEventListener('scroll', throttle(function () {
            EventService.publish('broadcastWindowScroll');
        }, 250));
        window.addEventListener('resize', debounce(function () {
            EventService.publish('broadcastWindowResize');
        }, 250));
        window.addEventListener('beforeprint', () => {
            EventService.publish('broadcastBeforePrint');
        });
        document.body.addEventListener('click', (e) => {
            EventService.publish('broadcastBodyClicked', e);
        });
    }
}
export default new WindowEventService();
