export class EventService {
    static subscribe(name, callback, context) {
        if (!Array.isArray(this.subscriptionList[name])) {
            this.subscriptionList[name] = [];
        }
        this.subscriptionList[name].push(callback.bind(context));
        return {
            name,
            subscriptionNumber: this.subscriptionList[name].length - 1,
        };
    }
    static publish(name, ...args) {
        if (this.subscriptionList[name]) {
            this.subscriptionList[name].forEach(fn => fn(args));
        }
    }
    static unsubscribe(subscription) {
        const { name, subscriptionNumber } = subscription;
        this.subscriptionList[name].splice(subscriptionNumber, 1);
    }
}
EventService.subscriptionList = {};
