export const getFirstAndLastFocusableElement = (root) => {
    const focusableElements = Array.from(root.querySelectorAll('a[href], button, input, textarea, select, details, [tabindex]:not([tabindex="-1"])')).filter(el => !el.hasAttribute('disabled') && el.offsetParent);
    return { first: focusableElements[0], last: focusableElements[focusableElements.length - 1] };
};
